import './App.css';
import Display from './components/Display';
import DrumPad from './components/DrumPad';
import drumObjs from './drumObj';
import { useState } from 'react';
import { useEffect } from 'react';

function App() {

  const [ content, setContent ] = useState( "display" )

  useEffect(() => 
    document.addEventListener('keydown', (event) => {
      playAudio(event.key.toUpperCase())
    })
  );

  function playAudio({ select }) {
    const audio = document.getElementById({ select });
    console.log(audio);
    audio.play();
  };

  return (
    <div className="App" >
      <h1 className="title">Drum Machine</h1>
      <div id="drum-machine" >
        <Display 
          displayed= { content } 
        />
        { drumObjs.map(( drum ) => 
        <DrumPad 
          keyName={ drum.audioSrc }
          buttonName={ drum.buttonName } 
          audioName={ drum.audioName } 
          audioSrc={ drum.audioSrc }
          Click= { () => { 
            playAudio( drum.audioSrc )
           } }
        />
        )}
      </div>
    </div>
  );
}

export default App;
