import './App.css';
import Display from './components/Display';
import DrumPad from './components/DrumPad';
import drumObjs from './drumObj';
import { useState } from 'react';
import { useEffect } from 'react';
function App() {

  const [ content, setContent ] = useState( "display" )

  useEffect(() => 
    document.addEventListener('keydown', (event) => {
      playAudio(event.key.toUpperCase())
    })
  );

  const handleChange = (event) => {
    const obj = () => {
      switch (drumObjs){
        case "Q":
          return 0;
        case "W":
          return 1;
        case "E":
          return 2;
        case "A":
          return 3;
        case "S":
          return 4;
        case "D":
          return 5;
        case "Z":
          return 6;
        case "X":
          return 7;
        case "C":
          return 8;
      }
    }
    return (
     setContent(drumObjs[obj].audioName),
     playAudio(drumObjs[obj].buttonName)
  )};

  const playAudio = ({ select }) => {
    const audio = document.getElementById({ select });
    console.log(audio);
    audio.play();
  };

  return (
    <div className="App" >
      <h1 className="title">Drum Machine</h1>
      <div id="drum-machine" >
        <Display 
          displayed= { content } 
        />
        { drumObjs.map(( drum ) => 
        <DrumPad 
          buttonName={ drum.buttonName } 
          audioName={ drum.audioName } 
          audioSrc={ drum.audioSrc }
          Click= { handleChange }
        />
        )}
      </div>
    </div>
  );
}

export default App;
