import './App.css';
import Display from './components/Display';
import DrumPad from './components/DrumPad';
import drumObjs from './drumObj';
import { useState } from 'react';

function App() {

  const [ content, setContent ] = useState( "display" )

  const HandleChange = ({ audioName }) => setContent({ audioName }) ;

  return (
    <div className="App" >
      <h1 className="title">Drum Machine</h1>
      <div id="drum-machine" >
        <Display 
          displayed= { content } 
        />
        <DrumPad 
          buttonName={drumObjs[0].buttonName} 
          audioName={drumObjs[0].audioName} 
          audioSrc={drumObjs[0].audioSrc}
          CLick= { HandleChange }
        />
        <DrumPad 
          buttonName={drumObjs[1].buttonName} 
          audioName={drumObjs[1].audioName}
          audioSrc={drumObjs[1].audioSrc}
          CLick= { HandleChange }
        />
        <DrumPad 
          buttonName={drumObjs[2].buttonName} 
          audioName={drumObjs[2].audioName}
          audioSrc={drumObjs[2].audioSrc}
          CLick= { HandleChange }
        />
        <DrumPad 
          buttonName={drumObjs[3].buttonName}
          audioName={drumObjs[3].audioName} 
          audioSrc={drumObjs[3].audioSrc}
          CLick= { HandleChange }
        />
        <DrumPad 
          buttonName={drumObjs[4].buttonName}
          audioName={drumObjs[4].audioName} 
          audioSrc={drumObjs[4].audioSrc}
          CLick= { HandleChange }
        />
        <DrumPad 
          buttonName={drumObjs[5].buttonName} 
          audioName={drumObjs[5].audioName} 
          audioSrc={drumObjs[5].audioSrc}
          CLick= { HandleChange }
        />
        <DrumPad 
          buttonName={drumObjs[6].buttonName} 
          audioName={drumObjs[6].audioName} 
          audioSrc={drumObjs[6].audioSrc}
          CLick= { HandleChange }
        />
        <DrumPad 
          buttonName={drumObjs[7].buttonName} 
          audioName={drumObjs[7].audioName} 
          audioSrc={drumObjs[7].audioSrc}
          CLick= { HandleChange }
        />
        <DrumPad 
          buttonName={drumObjs[8].buttonName} 
          audioName={drumObjs[8].audioName} 
          audioSrc={drumObjs[8].audioSrc}
          CLick= { HandleChange }
        />
      </div>
    </div>
  );
}

export default App;
